INSERT INTO `oc_informationf_description` (`information_id`, `language_id`, `title`, `description`, `meta_title`, `meta_description`, `meta_keyword`) VALUES
(8, 4, 'Виробництво', '&lt;div class=&quot;proiz-page container&quot;&gt;\r\n&lt;div class=&quot;section-top&quot;&gt;\r\n&lt;div class=&quot;image-main&quot;&gt;\r\n&lt;img src=&quot;/image/bags-1.jpg&quot; alt=&quot;&quot;&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;image-text&quot;&gt;\r\n&lt;h1&gt;НАШЕ ПРОИЗВОДСТВО&lt;/h1&gt;\r\nСВЕЖИЙ ВЗГЛЯД НА ПРОИЗВОДСТВО СУМОК\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;section-content-1&quot;&gt;\r\n&lt;div class=&quot;top-text&quot;&gt;\r\nНа каждой ступени изготовления, наши изделия проходят жесткий контроль качества. Отлаженная модель производства. Своевременный выпуск готовых изделий. Современное оборудование. Вот условия нужные для передового производства.\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n\r\n&lt;div class=&quot;proiz-colls&quot;&gt;\r\n	&lt;div class=&quot;section-content-2&quot;&gt;\r\n\r\n	&lt;div class=&quot;section-text&quot;&gt;\r\n	&lt;h3&gt;раскройный цех №1&lt;/h3&gt;\r\n	&lt;p&gt;Для производства используем только лучшую лицевую кожу. Используем различные методики раскроя для достижения наилучшего результата.&lt;/p&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;section-img&quot;&gt;\r\n	&lt;img src=&quot;/image/bags-3.jpg&quot; alt=&quot;&quot;&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;section-text&quot;&gt;\r\n	&lt;h3&gt;ШВЕЙНЫЙ ЦЕХ №3&lt;/h3&gt;\r\n	&lt;p&gt;Контролируем каждый стежок строчки. Нитки и фурнитуру покупаем исключительно в Италии.&lt;/p&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;section-img&quot;&gt;\r\n	&lt;img src=&quot;/image/bags-5.jpg&quot; alt=&quot;&quot;&gt;\r\n	&lt;/div&gt;\r\n\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;section-content-3&quot;&gt;\r\n\r\n	&lt;div class=&quot;section-img&quot;&gt;\r\n	&lt;img src=&quot;/image/bags-2.jpg&quot; alt=&quot;&quot;&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;section-text&quot;&gt;\r\n	&lt;h3&gt;ЗАГОТОвИТЕЛЬНЫЙ ЦЕХ №2&lt;/h3&gt;\r\n	&lt;p&gt;Тщательно подготавливаем каждую деталь кроя. Точность и щепетильность — главное правило.&lt;/p&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;section-img&quot;&gt;\r\n	&lt;img src=&quot;/image/bags-4.jpg&quot; alt=&quot;&quot;&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;section-text&quot;&gt;\r\n	&lt;h3&gt;ОТДЕЛОЧНЫЙ ЦЕХ №4&lt;/h3&gt;\r\n	&lt;p&gt;Цех, где происходят чудеса. Наши изделия обрабатываются исключительно итальянскими красками.&lt;/p&gt;\r\n	&lt;/div&gt;\r\n\r\n	&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;', 'Наше виробництво', '', ''),
